{
		"_comment": "IMPORTANT : go to the wiki page to know about options configuration https://github.com/simogeo/Filemanager/wiki/Filemanager-configuration-file",
    "options": {
        "culture": "en",
        "lang": "php",
        "defaultViewMode": "grid",
        "autoload": true,
        "showFullPath": false,
        "browseOnly": false,
        "showConfirmation": true,
        "showThumbs": true,
        "generateThumbnails": true,
        "searchBox": true,
        "listFiles": true,
        "fileSorting": "default",
        "chars_only_latin": true,
        "dateFormat": "d M Y H:i",
        "serverRoot": true,
        "fileRoot": "app/webroot/files/",
        "relPath": "/files/",
        "logger": false,
        "capabilities": ["select", "download", "rename", "move", "delete"],
        "plugins": []
    },
    "security": {
        "uploadPolicy": "DISALLOW_ALL",
        "uploadRestrictions": [
            "bmp", 
            "gif", 
            "jpe", 
            "jpeg", 
            "jpg", 
            "png", 
            "svg", 
            "webp",

            "doc", 
            "docx", 
            "epub", 
            "pdf", 
            "ppt", 
            "pptx", 
            "xls", 
            "xlsx", 

            "7z", 
            "bz2", 
            "gz", 
            "jar", 
            "rar", 
            "tar", 
            "tar.gz", 
            "tar.bz2", 
            "tgz", 
            "zip", 

            "3gp", 
            "asf", 
            "avi", 
            "divx", 
            "flv", 
            "mkv", 
            "mov", 
            "mp4", 
            "mpeg", 
            "mpg", 
            "ogv", 
            "rm", 
            "rmvb", 
            "swf", 
            "vid", 
            "webm", 
            "wmv", 
            "xvid",

            "aac", 
            "mid", 
            "midi", 
            "mogg", 
            "mp3", 
            "ogg", 
            "wav", 
            "wave", 
            "wma",

            "csv", 
            "torrent", 
            "txt", 
            "xml",

            "odp",
            "ods",
            "odt",
            "rtf"
        ]
    },
    "upload": {
        "overwrite": false,
        "imagesOnly": false,
        "fileSizeLimit": 60
    },
    "exclude": {
        "unallowed_files": [
            ".htaccess"
        ],
        "unallowed_dirs": [
            "_thumbs",
            ".CDN_ACCESS_LOGS",
            "cloudservers"
        ],
        "unallowed_files_REGEXP": "/^\\./uis",
        "unallowed_dirs_REGEXP": "/^\\./uis"
    },
    "images": {
        "imagesExt": [
            "bmp", 
            "gif", 
            "jpe", 
            "jpeg", 
            "jpg", 
            "png", 
            "svg", 
            "webp"
        ]
    },
    "videos": {
        "showVideoPlayer": true,
        "videosExt": [
            "3gp", 
            "asf", 
            "avi", 
            "divx", 
            "flv", 
            "mkv", 
            "mov", 
            "mp4", 
            "mpeg", 
            "mpg", 
            "ogv", 
            "rm", 
            "rmvb", 
            "swf", 
            "vid", 
            "webm", 
            "wmv", 
            "xvid"
        ],
        "videosPlayerWidth": 400,
        "videosPlayerHeight": 222
    },
    "audios": {
        "showAudioPlayer": true,
        "audiosExt": [
            "aac", 
            "mid", 
            "midi", 
            "mogg", 
            "mp3", 
            "ogg", 
            "wav", 
            "wave", 
            "wma"
        ]
    },
    "extras": {
        "extra_js": [],
        "extra_js_async": true
    },
    "icons": {
        "path": "images/fileicons/",
        "directory": "_Open.png",
        "default": "default.png"
    }
}